import React, {Fragment, useState} from "react";
import {Link} from "react-router-dom";
import brand from "../../images/brand.PNG";
import {useDispatch} from "react-redux";
import {registerUser} from "../../redux/user/userActions";
import {useHistory} from 'react-router-dom';

let Register = () => {
    let dispatch = useDispatch();
    let history = useHistory();

    let [user , setUser] = useState({
        name : '',
        email : '',
        password : ''
    });

    let submitRegister = (e) => {
        e.preventDefault();
        dispatch(registerUser(user,history));
    };

    return (
        <Fragment>
            {/*<pre>{JSON.stringify(user)}</pre>*/}
            <div className="container mt-3">
                <div className="row">
                    <div className="col-md-4 m-auto">
                        <div className="card animated jello">
                            <div className="card-header bg-brown text-white">
                                <h3>Register Here</h3>
                            </div>
                            <div className="card-body bg-light-brown">
                                <form onSubmit={submitRegister}>
                                    <div className="form-group">
                                        <input
                                            value={user.name}
                                            onChange={e => setUser({...user , name : e.target.value})}
                                            type="text" className="form-control" placeholder="Name"/>
                                    </div>
                                    <div className="form-group">
                                        <input
                                            value={user.email}
                                            onChange={e => setUser({...user , email : e.target.value})}
                                            type="email" className="form-control" placeholder="Email"/>
                                    </div>
                                    <div className="form-group">
                                        <input
                                            value={user.password}
                                            onChange={e => setUser({...user , password : e.target.value})}
                                            type="password" className="form-control" placeholder="Password"/>
                                    </div>
                                    <div>
                                        <input type="submit" value="Register" className="btn btn-brown btn-sm"/>
                                    </div>
                                    <p className="text-white">Already have an account ?
                                        <Link to="/login" className="text-info font-weight-bold"> Login</Link>
                                    </p>
                                </form>
                            </div>
                            <div className="card-footer text-center">
                                <img src={brand} alt="" width="150" height="40"/>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </Fragment>
    );
};
export default Register;
